package com.example.john.frescodemo;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.graphics.drawable.Drawable;
import android.os.Bundle;
import android.support.v7.app.AppCompatActivity;

import com.example.john.frescodemo.utils.ImageLoadFresco;
import com.facebook.drawee.view.SimpleDraweeView;

import butterknife.BindView;
import butterknife.ButterKnife;
import butterknife.OnClick;

public class MainActivity extends AppCompatActivity {

    @BindView(R.id.view_1)
    SimpleDraweeView mView1;
    @BindView(R.id.view_2)
    SimpleDraweeView mView2;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        ButterKnife.bind(this);

        String uri = "data:image/jpeg;base64,";
        Drawable drawable = getResources().getDrawable(R.mipmap.ic_launcher);
        Bitmap bitmap = BitmapFactory.decodeResource(getResources(), R.mipmap.ic_launcher);
        ImageLoadFresco build = new ImageLoadFresco.LoadImageFrescoBuilder(this, mView1, uri)
                .setIsCircle(true)
                .setPlaceHolderImage(drawable).build();

        new ImageLoadFresco.LoadImageFrescoBuilder(this, mView2, uri+"dsd").setPlaceHolderImage(drawable)
                .setRetryImage(drawable).setFailureImage(drawable).build();

    }

    @OnClick(R.id.btn_1)
    public void onClick() {

    }
}
